import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./components/HomePage";
import Login from "./components/Loginpage";
import SignupPage from "./components/signuppage"; 

// Assuming that you have a method to check if the user is authenticated
import { auth } from "./firebase"; // Firebase auth import

// Protected Route component to guard the homepage
const ProtectedRoute = ({ children }) => {
  if (!auth.currentUser) {
    // If user is not logged in, redirect to login page
    return <Navigate to="/loginp" />;
  }
  return children;
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Route for HomePage, protected for authenticated users */}
        <Route
          path="/homepage"
          element={
            <ProtectedRoute>
              <HomePage />
            </ProtectedRoute>
          }
        />
        
        {/* Route for LoginPage */}
        <Route path="/loginpage" element={<Login />} />
        
        {/* Route for SignupPage */}
        <Route path="/signuppage" element={<SignupPage />} />
        
        {/* Default Route to redirect to LoginPage if the user visits the root */}
        <Route path="/" element={<Navigate to="/loginpage" />} />
      </Routes>
    </Router>
  );
}

export default App;
