import React, { useState } from 'react';
import { useWebRTC } from './useWebRTC';
import  VideoGrid  from './VideoGrid';
import  Controls  from './Controls';
import { Video, Users } from 'lucide-react';

function App() {
  const {
    localStream,
    peers,
    roomId,
    createRoom,
    joinRoom,
    leaveRoom,
    toggleAudio,
    toggleVideo,
  } = useWebRTC();

  const [joinRoomId, setJoinRoomId] = useState('');
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);

  const handleToggleAudio = () => {
    toggleAudio();
    setIsAudioEnabled(!isAudioEnabled);
  };

  const handleToggleVideo = () => {
    toggleVideo();
    setIsVideoEnabled(!isVideoEnabled);
  };

  if (!roomId) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="max-w-md w-full space-y-8 p-8 bg-white rounded-xl shadow-lg">
          <div className="text-center">
            <h2 className="mt-6 text-3xl font-bold text-gray-900">
              Video Conference
            </h2>
            <p className="mt-2 text-sm text-gray-600">
              Create a new room or join an existing one
            </p>
          </div>
          <div className="space-y-4">
            <button
              onClick={createRoom}
              className="w-full flex items-center justify-center px-4 py-3 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <Video className="w-5 h-5 mr-2" />
              Create New Room
            </button>
            <div className="flex space-x-2">
              <input
                type="text"
                value={joinRoomId}
                onChange={(e) => setJoinRoomId(e.target.value)}
                placeholder="Enter Room ID"
                className="flex-1 appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
              />
              <button
                onClick={() => joinRoom(joinRoomId)}
                disabled={!joinRoomId}
                className="flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:bg-gray-400"
              >
                <Users className="w-5 h-5 mr-2" />
                Join
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900">
      <div className="p-4">
        <div className="mb-4 flex items-center justify-between">
          <h1 className="text-xl text-white font-semibold">Room ID: {roomId}</h1>
          <button
            onClick={() => {
              navigator.clipboard.writeText(roomId);
            }}
            className="px-4 py-2 text-sm text-white bg-gray-700 rounded-md hover:bg-gray-600"
          >
            Copy Room ID
          </button>
        </div>
        <VideoGrid localStream={localStream} peers={peers} />
      </div>
      <Controls
        isAudioEnabled={isAudioEnabled}
        isVideoEnabled={isVideoEnabled}
        onToggleAudio={handleToggleAudio}
        onToggleVideo={handleToggleVideo}
        onLeaveCall={leaveRoom}
      />
    </div>
  );
}

export default App;
