import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom"; // useNavigate instead of useHistory
import { FaBlogger, FaComments, FaUsers, FaPhoneAlt, FaInfoCircle } from "react-icons/fa";
import { auth, logout } from "../firebase"; // Import the logout function from firebase.js
import "../components/HomePage.css";
import heroImage2 from "../components/images/Flux_Dev_A_warm_and_inviting_scene_featuring_a_diverse_group_o_3 1.png";
import heroImage from "../components/images/WhatsApp_Image_2025-02-21_at_18.09.08_fdcede51-removebg-preview.png";

const features = [
  { icon: <FaComments size={30} />, title: "Chat" },
  { icon: <FaBlogger size={30} />, title: "Blog" },
  { icon: <FaUsers size={30} />, title: "Join" },
  { icon: <FaPhoneAlt size={30} />, title: "Call" },
  { icon: <FaInfoCircle size={30} />, title: "Info" },
];

const testimonials = [
  {
    name: "Alice Smith",
    role: "Software Developer",
    testimonial: "MuteCall has revolutionized the way I communicate with my team. The real-time sign language detection is amazing!",
  },
  {
    name: "John Doe",
    role: "Product Manager",
    testimonial: "A lifesaver for people who rely on sign language for communication. It makes work and personal calls so much easier.",
  },
  {
    name: "Emma Johnson",
    role: "Community Leader",
    testimonial: "MuteCall is a game changer. It's empowering and helping bridge gaps in communication across different communities.",
  },
];

const LandingPage = () => {
  const [activeTestimonial, setActiveTestimonial] = useState(0);
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Track authentication state
  const navigate = useNavigate(); // Declare navigate function

  // Refs for sections to scroll to
  const heroRef = useRef(null);
  const aboutRef = useRef(null);
  const featuresRef = useRef(null);
  const testimonialsRef = useRef(null);

  // Refs for video call setup
  const localVideoRef = useRef(null); // To display the local video
  const remoteVideoRef = useRef(null); // To display the remote video

  // WebRTC peer connection setup
  const peerConnection = useRef(null);
  const mediaStream = useRef(null);

  // Listen for auth state changes
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setIsAuthenticated(true);
      } else {
        setIsAuthenticated(false);
      }
    });

    return () => unsubscribe(); // Clean up on component unmount
  }, []);

  const handleTestimonialChange = (index) => {
    setActiveTestimonial(index);
  };

  const handleJoinCall = () => {
    if (!isAuthenticated) {
      navigate("/loginpage"); // Redirect to login if not authenticated
      return;
    }

    const meetingCode = document.getElementById("meeting-code-input").value;
    if (meetingCode) {
      navigate("/MeetingRoom"); // Redirect to the meeting room with entered meeting code
    }
  };

  const handleStartCall = async () => {
    if (!isAuthenticated) {
      navigate("/loginpage"); // Redirect to login if not authenticated
      return;
    }

    // Initialize WebRTC connection when starting the call
    await startVideoCall();
  };

  const startVideoCall = async () => {
    try {
      // Get user media (camera and microphone)
      mediaStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });

      // Display the local video stream
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = mediaStream.current;
      }

      // Create peer connection
      peerConnection.current = new RTCPeerConnection();

      // Add local stream to peer connection
      mediaStream.current.getTracks().forEach((track) => {
        peerConnection.current.addTrack(track, mediaStream.current);
      });

      // Handle receiving remote stream
      peerConnection.current.ontrack = (event) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
        }
      };

      // You would need signaling server logic here for offer/answer exchange and ICE candidate handling.
      // This can be done using WebSocket or any other signaling protocol

      console.log("Video call started");
    } catch (err) {
      console.error("Error starting video call:", err);
    }
  };

  const scrollToSection = (sectionRef) => {
    sectionRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleHomeClick = () => {
    scrollToSection(heroRef); // Scroll to the Hero Section
  };

  // Logout Handler
  const handleLogout = async () => {
    try {
      await logout(); // Call the logout function from firebase.js
      setIsAuthenticated(false); // Update authentication state
      navigate("/"); // Redirect to homepage after logout
    } catch (error) {
      console.error("Error during logout:", error.message);
    }
  };

  // Scroll to hero section on page load (if it's a fresh load or after refresh)
  useEffect(() => {
    scrollToSection(heroRef); // Make sure we start at the Hero section when the page loads or is refreshed
  }, []);

  return (
    <div className="landing-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">MuteCall</div>
        <div className="nav-links">
          <button onClick={handleHomeClick}>Home</button> {/* Scroll to Hero section */}
          <button onClick={() => scrollToSection(aboutRef)}>About</button> {/* Scroll to About section */}
          <button onClick={() => scrollToSection(featuresRef)}>Feature</button> {/* Scroll to Features section */}
          <button onClick={() => scrollToSection(testimonialsRef)}>Blog</button>
        </div>
        {isAuthenticated ? (
          <button onClick={handleLogout} className="logout-btn">Logout</button> // Show Logout button
        ) : (
          <Link to="/loginpage" className="signup-login-btn">Sign Up / Login</Link> // Show Login/Signup button
        )}
      </nav>

      <section ref={heroRef} className="hero">
        <div className="hero-text">
          <h1 className="hero-title">
            Bridging Communication Gaps with AI-Powered Sign Language Detection!
          </h1>
          <p className="hero-subtitle">Making conversations effortless with smart sign language detection.</p>
          <div className="hero-buttons">
            <button className="primary-btn" onClick={handleStartCall}>Start Call</button> {/* Only starts call when logged in */}
            <input
              type="text"
              id="meeting-code-input"
              placeholder="Enter Meeting Code or Link"
              className="meeting-code-input"
            />
            <button className="secondary-btn" onClick={handleJoinCall}>Join Call</button> {/* Only joins call when logged in */}
          </div>
        </div>
        <div className="hero-image-zoom">
          <img src={heroImage2} alt="AI-Powered Communication" />
        </div>
      </section>

      {/* Video Call Section */}
      <section className="video-call">
        <video ref={localVideoRef} autoPlay muted></video> {/* Local video */}
        <video ref={remoteVideoRef} autoPlay></video> {/* Remote video */}
      </section>

      {/* Image and Text Section */}
      <div ref={aboutRef} className="image-container">
        <img src={heroImage} alt="Image" className="zoom-image" />
        <div className="text-container">
          <h1 className="image-title">INTRODUCTION</h1>
          <h2 className="image-subtitle">Smarter Conversations</h2>
          <p className="image-info">Communication is more than just words. Our AI-powered system recognizes and translates sign language gestures in real-time, making interactions more inclusive and meaningful. Whether you're a beginner or an expert, our platform is here to support you.</p>
        </div>
      </div>

      {/* Other Sections */}
      <section ref={featuresRef} className="features">
        <h2 className="section-title">Features Designed for the Deaf Community</h2>
        <div className="feature-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-item">
              {feature.icon}
              <span>{feature.title}</span>
            </div>
          ))}
        </div>
      </section>

      <section ref={testimonialsRef} className="testimonials">
        <h2 className="section-title">What Our Users Say</h2>
        <div className="testimonial-slider">
          <div className="testimonial-text">
            <p>"{testimonials[activeTestimonial].testimonial}"</p>
            <h3>{testimonials[activeTestimonial].name}</h3>
            <span>{testimonials[activeTestimonial].role}</span>
          </div>
          <div className="testimonial-navigation">
            <button
              onClick={() => handleTestimonialChange((activeTestimonial - 1 + testimonials.length) % testimonials.length)}
              className="testimonial-nav-btn"
            >
              Previous
            </button>
            <button
              onClick={() => handleTestimonialChange((activeTestimonial + 1) % testimonials.length)}
              className="testimonial-nav-btn"
            >
              Next
            </button>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2025 MuteCall. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
