import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaBlogger, FaComments, FaUsers, FaPhoneAlt, FaInfoCircle } from "react-icons/fa";
import "../components/HomePage.css";
import heroImage from "../components/images/Flux_Dev_A_warm_and_inviting_scene_featuring_a_diverse_group_o_3 1.png";
import heroImage2 from "../components/images/Flux_Dev_A_warm_and_inviting_scene_featuring_a_diverse_group_o_3 1.png";

const features = [
  { icon: <FaComments size={30} />, title: "Chat" },
  { icon: <FaBlogger size={30} />, title: "Blog" },
  { icon: <FaUsers size={30} />, title: "Join" },
  { icon: <FaPhoneAlt size={30} />, title: "Call" },
  { icon: <FaInfoCircle size={30} />, title: "Info" },
];

const testimonials = [
  {
    name: "Alice Smith",
    role: "Software Developer",
    testimonial: "MuteCall has revolutionized the way I communicate with my team. The real-time sign language detection is amazing!",
  },
  {
    name: "John Doe",
    role: "Product Manager",
    testimonial: "A lifesaver for people who rely on sign language for communication. It makes work and personal calls so much easier.",
  },
  {
    name: "Emma Johnson",
    role: "Community Leader",
    testimonial: "MuteCall is a game changer. It's empowering and helping bridge gaps in communication across different communities.",
  },
];

const LandingPage = () => {
  const [activeTestimonial, setActiveTestimonial] = useState(0);

  const handleTestimonialChange = (index) => {
    setActiveTestimonial(index);
  };

  return (
    <div className="landing-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">MuteCall</div>
        <div className="nav-links">
          {['Home', 'About', 'Feature', 'Blog'].map((item) => (
            <Link key={item} to={`/${item.toLowerCase()}`}>{item}</Link>
          ))}
        </div>
        <Link to="/signup" className="signup-btn">Sign Up / Login</Link>
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-text">
          <h1 className="hero-title">
            Bridging Communication Gaps with AI-Powered Sign Language Detection!
          </h1>
          <p className="hero-subtitle">Making conversations effortless with smart sign language detection.</p>
          <div className="hero-buttons">
            {['Start Call', 'Join Call'].map((btn, index) => (
              <button key={index} className={index === 0 ? "primary-btn" : "secondary-btn"}>{btn}</button>
            ))}
          </div>
        </div>
        <div className="hero-image">
          <img src={hero} alt="AI-Powered Communication" />
        </div>
      </section>

      {/* Add Image between the buttons */}
      <div className="image-container">
        <img src={heroImage} alt="Interactive Demo" />
      </div>

      {/* Features Section */}
      <section className="features">
        <h2 className="section-title">Features Designed for the Deaf Community</h2>
        <div className="feature-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-item">
              {feature.icon}
              <span>{feature.title}</span>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonial Section */}
      <section className="testimonials">
        <h2 className="section-title">What Our Users Say</h2>
        <div className="testimonial-slider">
          <div className="testimonial-text">
            <p>"{testimonials[activeTestimonial].testimonial}"</p>
            <h3>{testimonials[activeTestimonial].name}</h3>
            <span>{testimonials[activeTestimonial].role}</span>
          </div>
          <div className="testimonial-nav">
            <button 
              onClick={() => handleTestimonialChange((activeTestimonial - 1 + testimonials.length) % testimonials.length)} 
              className="prev-btn"
            >
              &#60;
            </button>
            <button 
              onClick={() => handleTestimonialChange((activeTestimonial + 1) % testimonials.length)} 
              className="next-btn"
            >
              &#62;
            </button>
          </div>
        </div>
      </section>

      {/* Interactive Demo Section */}
      <section className="demo">
        <h2 className="section-title">Try MuteCall in Action</h2>
        <p>Click below to experience how MuteCall bridges communication gaps with live sign language translation.</p>
        <Link to="/demo" className="cta-btn">Start Demo</Link>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>© 2025 MuteCall. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
