import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./VideoCallPage.css"; // Import styles

const VideoCallPage = () => {
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const peerConnection = useRef(null);
  const mediaStream = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Start video call when component mounts
    startVideoCall();
    return () => {
      stopVideoCall(); // Cleanup on unmount
    };
  }, []);

  const startVideoCall = async () => {
    try {
      // Get media stream
      mediaStream.current = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      // Set local video stream
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = mediaStream.current;
      }

      // Create PeerConnection
      peerConnection.current = new RTCPeerConnection();
      mediaStream.current.getTracks().forEach((track) => {
        peerConnection.current.addTrack(track, mediaStream.current);
      });

      // Set up the remote video stream
      peerConnection.current.ontrack = (event) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
        }
      };

      console.log("Video call started");
    } catch (error) {
      console.error("Error starting video call:", error);
    }
  };

  const stopVideoCall = () => {
    // Close peer connection and stop the stream
    if (peerConnection.current) {
      peerConnection.current.close();
    }
    if (mediaStream.current) {
      mediaStream.current.getTracks().forEach((track) => track.stop());
    }
    navigate("/Home"); // Redirect to landing page after call ends
  };

  const toggleMute = () => {
    setIsMuted((prev) => !prev);
    mediaStream.current.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
  };

  const toggleVideo = () => {
    setIsVideoOn((prev) => !prev);
    mediaStream.current.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
  };

  return (
    <div className="video-call-page">
      <div className="video-container">
        {/* Local Video */}
        <div className="local-video">
          <video ref={localVideoRef} autoPlay muted className="video" />
        </div>

        {/* Remote Video */}
        <div className="remote-video">
          <video ref={remoteVideoRef} autoPlay className="video" />
        </div>
      </div>

      <div className="controls">
        <button className="control-btn" onClick={toggleMute}>
          {isMuted ? "Unmute" : "Mute"}
        </button>
        <button className="control-btn" onClick={toggleVideo}>
          {isVideoOn ? "Turn Off Video" : "Turn On Video"}
        </button>
        <button className="end-call-btn" onClick={stopVideoCall}>
          End Call
        </button>
      </div>
    </div>
  );
};

export default VideoCallPage;
