import React from "react";
import { Link } from "react-router-dom";
import { FaBlogger, FaComments, FaUsers, FaPhoneAlt, FaInfoCircle } from "react-icons/fa";
import "../components/HomePage.css";
import heroImage from "../components/images/Flux_Dev_A_warm_and_inviting_scene_featuring_a_diverse_group_o_3 1.png";
const features = [
  { icon: <FaComments size={30} />, title: "Chat", img: featureImg1 },
  { icon: <FaBlogger size={30} />, title: "Blog", img: featureImg2 },
  { icon: <FaUsers size={30} />, title: "Join", img: featureImg3 },
  { icon: <FaPhoneAlt size={30} />, title: "Call" },
  { icon: <FaInfoCircle size={30} />, title: "Info" },
];

const LandingPage = () => {
  return (
    <div className="landing-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">MuteCall</div>
        <div className="nav-links">
          {['Home', 'About', 'Feature', 'Blog'].map((item) => (
            <Link key={item} to={`/${item.toLowerCase()}`}>{item}</Link>
          ))}
        </div>
        <Link to="/signup" className="signup-btn">Sign Up</Link>
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-text">
          <h1 className="hero-title">
            Bridging Communication Gaps with <br /> AI-Powered Sign Language Detection!
          </h1>
          <p className="hero-subtitle">Making conversations effortless with smart sign language detection.</p>
          <div className="hero-buttons">
            {['Get a Demo', 'Sign Up'].map((btn, index) => (
              <button key={index} className={index === 0 ? "primary-btn" : "secondary-btn"}>{btn}</button>
            ))}
          </div>
        </div>
        <div className="hero-image">
          <img src={heroImage} alt="AI-Powered Communication" />
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <h2 className="section-title">Features Designed for the Deaf Community</h2>
        <div className="feature-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-item">
              {feature.img && <img src={feature.img} alt={feature.title} />}
              {feature.icon}
              <span>{feature.title}</span>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2025 MuteCall. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
