import React, { useState } from 'react';
import './meet.css'; // Importing CSS styles
import { Video, Users } from 'lucide-react';

function App() {
  const [joinRoomId, setJoinRoomId] = useState('');
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);

  const handleToggleAudio = () => {
    setIsAudioEnabled(!isAudioEnabled);
  };

  const handleToggleVideo = () => {
    setIsVideoEnabled(!isVideoEnabled);
  };

  const roomId = 'roomIdExample'; // Placeholder for roomId (Replace with actual logic)
  
  const createRoom = () => {
    console.log("Creating Room...");
  };

  const joinRoom = (roomId) => {
    console.log("Joining Room with ID: ", roomId);
  };

  if (!roomId) {
    return (
      <div className="app-container">
        <div className="form-container">
          <h2 className="title">Video Conference</h2>
          <p className="subtitle">Create a new room or join an existing one</p>
          <button onClick={createRoom} className="btn-create-room">
            <Video className="icon" />
            Create New Room
          </button>
          <div className="join-room-container">
            <input
              type="text"
              value={joinRoomId}
              onChange={(e) => setJoinRoomId(e.target.value)}
              placeholder="Enter Room ID"
              className="input-room-id"
            />
            <button
              onClick={() => joinRoom(joinRoomId)}
              disabled={!joinRoomId}
              className="btn-join-room"
            >
              <Users className="icon" />
              Join
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="call-container">
      <div className="room-header">
        <h1 className="room-id">Room ID: {roomId}</h1>
        <button
          onClick={() => {
            navigator.clipboard.writeText(roomId);
          }}
          className="btn-copy-room-id"
        >
          Copy Room ID
        </button>
      </div>
      {/* Add Video Grid Component Here */}
      {/* Add Controls Component Here */}
    </div>
  );
}

export default App;
