import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { IconButton, Fab, Box, Grid } from "@mui/material";
import { FaMicrophone, FaMicrophoneAltSlash, FaVideo, FaVideoSlash, FaPhoneAlt } from "react-icons/fa"; // Icons for controls
import styled from "@emotion/styled"; // Styled components for custom styles

// Styled components
const VideoContainer = styled(Box)(({
  display: "flex",
  justifyContent: "center",
  gap: "20px",
  position: "relative",
  flexWrap: "wrap",
}));

const LocalVideoWrapper = styled(Box)(({
  position: "absolute",
  top: "10px",
  left: "10px",
  width: "120px",
  height: "80px",
  backgroundColor: "rgba(0, 0, 0, 0.5)",
  borderRadius: "8px",
  overflow: "hidden",
  zIndex: 10,
}));

const Video = styled("video")({
  width: "100%",
  height: "100%",
  objectFit: "cover",
  borderRadius: "8px",
});

const ControlPanel = styled(Box)(({
  position: "fixed",
  bottom: "20px",
  left: "50%",
  transform: "translateX(-50%)",
  display: "flex",
  alignItems: "center",
  gap: "25px",
  zIndex: 10,
}));

const EndCallButton = styled(Fab)(({
  backgroundColor: "#f44336",
  color: "#fff",
  padding: "15px",
  fontSize: "20px",
  "&:hover": {
    backgroundColor: "#d32f2f",
  },
}));

const VideoCallPage = () => {
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const peerConnection = useRef(null);
  const mediaStream = useRef(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    startVideoCall();
    return () => {
      stopVideoCall(); // Cleanup on unmount
    };
  }, []);

  const startVideoCall = async () => {
    try {
      // Get media stream
      mediaStream.current = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      // Set local video stream
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = mediaStream.current;
      }

      // Create PeerConnection
      peerConnection.current = new RTCPeerConnection({
        iceServers: [{ urls: "stun:stun.l.google.com:19302" }], // Use a public STUN server for now
      });

      // Add local media tracks to peer connection
      mediaStream.current.getTracks().forEach((track) => {
        peerConnection.current.addTrack(track, mediaStream.current);
      });

      // Handle ICE candidate events
      peerConnection.current.onicecandidate = (event) => {
        if (event.candidate) {
          sendMessageToPeer({
            type: "candidate",
            candidate: event.candidate,
          });
        }
      };

      // Set up the remote video stream when a track is received
      peerConnection.current.ontrack = (event) => {
        setRemoteStreams((prevStreams) => [
          ...prevStreams,
          event.streams[0],
        ]);
      };

      // Create offer and set local description
      const offer = await peerConnection.current.createOffer();
      await peerConnection.current.setLocalDescription(offer);

      // Send offer to remote peer via signaling
      sendMessageToPeer({ type: "offer", offer });
      
      console.log("Video call started");
    } catch (error) {
      console.error("Error starting video call:", error);
    }
  };

  const stopVideoCall = () => {
    if (peerConnection.current) {
      peerConnection.current.close();
    }
    if (mediaStream.current) {
      mediaStream.current.getTracks().forEach((track) => track.stop());
    }
    navigate("/VideoCallPage");
  };

  // Signaling method to send messages to the remote peer
  const sendMessageToPeer = (message) => {
    console.log("Sending message to peer:", message);
  };

  const toggleMute = () => {
    setIsMuted((prev) => !prev);
    mediaStream.current.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
  };

  const toggleVideo = () => {
    setIsVideoOn((prev) => !prev);
    mediaStream.current.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
  };

  return (
    <Box sx={{ display: "flex", flexDirection: "column", alignItems: "center", backgroundColor: "#f5f5f5", height: "100vh" }}>
      {/* Video Container with Grid Layout */}
      <VideoContainer>
        {/* Local Video */}
        <LocalVideoWrapper>
          <Video ref={localVideoRef} autoPlay muted />
        </LocalVideoWrapper>

        {/* Remote Videos */}
        <Grid container spacing={2} sx={{ width: "100%", height: "100%" }}>
          {remoteStreams.map((stream, index) => (
            <Grid item xs={4} key={index}>
              <Box sx={{ backgroundColor: "black", borderRadius: "8px", width: "100%", height: "100%" }}>
                <Video ref={(ref) => (remoteVideoRef.current = ref)} autoPlay srcObject={stream} />
              </Box>
            </Grid>
          ))}
        </Grid>
      </VideoContainer>

      {/* Control Panel */}
      <ControlPanel>
        {/* Mute/Unmute Button */}
        <IconButton onClick={toggleMute} sx={{ backgroundColor: isMuted ? "#b0bec5" : "#2196f3", borderRadius: "50%", padding: "10px" }}>
          {isMuted ? <FaMicrophoneAltSlash size={20} color="white" /> : <FaMicrophone size={20} color="white" />}
        </IconButton>

        {/* Video On/Off Button */}
        <IconButton onClick={toggleVideo} sx={{ backgroundColor: isVideoOn ? "#4caf50" : "#f44336", borderRadius: "50%", padding: "10px" }}>
          {isVideoOn ? <FaVideo size={20} color="white" /> : <FaVideoSlash size={20} color="white" />}
        </IconButton>

        {/* End Call Button */}
        <EndCallButton onClick={stopVideoCall}>
          <FaPhoneAlt size={25} />
        </EndCallButton>
      </ControlPanel>
    </Box>
  );
};

export default VideoCallPage;
