import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { VideoGrid } from "./VideoGrid"; // Ensure these components are in the same folder or adjust paths
import { Controls } from "./Controls"; 
import { useWebRTC } from "./hooks/useWEBRTC"; // Custom hook for WebRTC logic

function MeetingRoom() {
  const {
    localStream,
    peers,
    roomId,
    createRoom,
    joinRoom,
    leaveRoom,
    toggleAudio,
    toggleVideo,
  } = useWebRTC();

  const [joinRoomId, setJoinRoomId] = useState("");
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const navigate = useNavigate();

  const handleToggleAudio = () => {
    toggleAudio();
    setIsAudioEnabled(!isAudioEnabled);
  };

  const handleToggleVideo = () => {
    toggleVideo();
    setIsVideoEnabled(!isVideoEnabled);
  };

  const handleCreateRoom = () => {
    createRoom();
  };

  const handleJoinRoom = () => {
    if (joinRoomId) {
      joinRoom(joinRoomId);
    }
  };

  const handleLeaveRoom = () => {
    leaveRoom();
    navigate("/homepage"); // Navigate back to homepage on leave
  };

  useEffect(() => {
    if (roomId) {
      console.log("Joined room:", roomId);
    }
  }, [roomId]);

  return (
    <div className="meeting-room-container">
      <div className="header">
        <h1>Room ID: {roomId}</h1>
        <button onClick={() => navigator.clipboard.writeText(roomId)}>
          Copy Room ID
        </button>
      </div>

      {!roomId ? (
        <div className="room-selection">
          <h2>Create or Join a Room</h2>
          <button onClick={handleCreateRoom}>Create New Room</button>
          <input
            type="text"
            value={joinRoomId}
            onChange={(e) => setJoinRoomId(e.target.value)}
            placeholder="Enter Room ID"
          />
          <button onClick={handleJoinRoom}>Join Room</button>
        </div>
      ) : (
        <div className="video-container">
          <VideoGrid localStream={localStream} peers={peers} />
        </div>
      )}

      <Controls
        isAudioEnabled={isAudioEnabled}
        isVideoEnabled={isVideoEnabled}
        onToggleAudio={handleToggleAudio}
        onToggleVideo={handleToggleVideo}
        onLeaveCall={handleLeaveRoom}
      />
    </div>
  );
}

export default MeetingRoom;
