import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./components/HomePage";
import Login from "./components/Loginpage";
import SignupPage from "./components/signuppage"; 

import { auth } from "./firebase"; // Firebase auth import
import { onAuthStateChanged } from "firebase/auth"; // Firebase function to track auth state changes

// Protected Route component to guard the homepage
const ProtectedRoute = ({ children }) => {
  if (!auth.currentUser) {
    // If user is not logged in, redirect to login page
    return <Navigate to="/loginpage" />;
  }
  return children;
};

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Track auth state changes
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    return () => unsubscribe(); // Cleanup on unmount
  }, []);

  const handleLogout = async () => {
    await auth.signOut();
  };

  return (
    <Router>
      <Routes>
        {/* Default Route to HomePage */}
        <Route path="/" element={<Navigate to="/homepage" />} />

        {/* Route for HomePage, with dynamic Login/Logout button */}
        <Route
          path="/homepage"
          element={
            <HomePage user={user} onLogout={handleLogout} />
          }
        />
        
        {/* Route for LoginPage */}
        <Route path="/loginpage" element={<Login />} />
        
        {/* Route for SignupPage */}
        <Route path="/signuppage" element={<SignupPage />} />
      </Routes>
    </Router>
  );
}

export default App;
